import React, { Component } from 'react'

export default class MultiSlider extends Component {
  constructor(props) {
    super(props)

    this.state = {
      images: [
        {url:"https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/aurora.jpg", description:"Description 1"},
        {url:"https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/aurora.jpg", description:"Description 2"},
        {url:"https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/aurora.jpg", description:"Description 3"},
        {url:"https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/aurora.jpg", description:"Description 4"},
        {url:"https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/aurora.jpg", description:"Description 5"},
        {url:"https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/aurora.jpg", description:"Description 6"},
        {url:"https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/aurora.jpg", description:"Description 7"},

      ],
      currentIndex: 0,
      translateValue: 0
    }
  }

  goToPrevSlide = () => {

  }

  goToNextSlide = () => {
    // Exiting the method early if we are at the end of the images array.
    // We also want to reset currentIndex and translateValue, so we return
    // to the first image in the array.
    if(this.state.currentIndex === this.state.images.length - 1) {
      return this.setState({
        currentIndex: 0,
        translateValue: 0
      })
    }

    // This will not run if we met the if condition above
    this.setState(prevState => ({
      currentIndex: prevState.currentIndex + 1,
      translateValue: prevState.translateValue + -(this.slideWidth())
    }));
  }

//   slideWidth = () => {
//      return document.querySelector('.slide').clientWidth
//   }

  render() {
    return (
      <div className="slider">

        <div className="slider-wrapper"
          style={{
            transform: `translateX(${this.state.translateValue}px)`,
            transition: 'transform ease-out 0.45s'
          }}>
            {
              this.state.images.map((image, i) => (
                <MultiSlide key={i} image={image.url} description={image.description} />
              ))
            }
        </div>

        <LeftArrow
         goToPrevSlide={this.goToPrevSlide}
        />

        <RightArrow
         goToNextSlide={this.goToNextSlide}
        />
      </div>
    );
  }
}

const MultiSlide = ({ image, description }) => {
    const slideStyles = {

        width: "33%",
        height: "120px",
        margin:"1% 1%",
        fontSize: "14px",
        textAlign: "center"
    }

  return <div className="slide" style={slideStyles}>
      <img src={image} alt={description}></img>
      <p>{description}</p>
  </div>
}

const LeftArrow = (props) => {
    return (
      <div className="backArrow arrow" onClick={props.goToPrevSlide}>
        <i className="arrow-image-left" aria-hidden="true"></i>
      </div>
    );
  }
  const RightArrow = (props) => {
    return (
      <div className="nextArrow arrow" onClick={props.goToNextSlide}>
        <i className="arrow-image-right" aria-hidden="true"></i>
      </div>
    );
  }
